$(function() {
    $('.datepicker').datetimepicker({
        language: 'hu',
        pickTime: false
    });
});

$(function() {
    $('.datetimepicker').datetimepicker({
        language: 'hu',
        pickSeconds: false
    });
});

$(function() {
    $('.timepicker').datetimepicker({
        language: 'hu',
        pickDate: false,
        pickSeconds: false
    });
});